// MIXINS

@mixin contract {
  position: fixed;
  width: $console-w-narrow;
  max-height: $console-maxh-narrow;
  overflow: hidden;
  padding: 0 1vw;
  animation: contract-console 2s;
  transition: all 300ms ease;
  background-image: url('/assets/interface.svg');
  background-repeat: no-repeat;
  background-size: 100% 100%;
}

@mixin expand {
  animation: expand-console 2s forwards;
}

html {
  position: relative;

}

html::before {
  content: "";
  position: fixed;
  background-image: url('/assets/city-image.jpg');
  background-repeat: no-repeat;
  background-attachment: fixed;
  background-position: center;
  background-size: 100% 100%;
  top: 0;
  left: 0;
  right: 0;
  bottom: 0;
  position: absolute;
  z-index: -3;
  filter: blur(3px);
  -webkit-filter: blur(3px);
}

.page-wrapper {
  display: flex;
  flex-direction: column;
  margin: 0 0.5rem;
}

.intro {
  order: 1;
}

.main {
  order: 3;
}

.sidebar {
  order: 2;
}

header,
footer,
.summary,
.preamble,
.explanation,
.participation,
.benefits,
.requirements,
.sidebar {
  border-width: $border-width;
  border-style: solid;
  margin: 1rem 0;
}

@media only screen and (min-width: $M) {
	.page-wrapper {
		margin: 0 5rem;
	}

	.wrapper {
		display: grid;
		grid-template-columns: 1fr 1fr;
	}

	.design-selection {
		grid-row: span 2;
		border-right-width: $border-width;
		border-right-style: solid;
		align-self: stretch; 
	}

	.zen-resources {
		grid-column: 2 / 3;
	}
}

@media only screen and (min-width: $L) {

  html::before {
    content: "";
    position: fixed;
    background-image: url('/assets/city-image.jpg');
    background-repeat: no-repeat;
    background-attachment: fixed;
    background-position: center;
    background-size: 100% 100%;
    top: 0;
    left: 0;
    width: 100vw;
    height: 100vh;
    position: absolute;
    z-index: -3;
    filter: blur(3px);
    -webkit-filter: blur(3px);
  }



  header,
  footer,
  .summary,
  .preamble,
  .explanation,
  .participation,
  .benefits,
  .requirements,
  .sidebar {
    margin: 0;
  }

  .intro {
    position: fixed;
    top: 0;
    left: 50vw;
    transform: translate(-50%, -95%);
    transition: transform 2s ease-in-out;
  }

  .intro::after {
    content: "CSS Zen Garden";
  }

  .intro:hover {
    transform: translate(-50%, 0);
  }

  header > *{
    margin: 1rem;
  }

  .sidebar {
    position: fixed;
    left: 0;
    top: 50vh;
    width: 11.5rem;
    height: 90vh;
    padding: 0.75rem;
    transform: translate(-12.5rem, -50%);
    transition: transform 2s ease-in-out;
    background-image: url('/assets/side-interface.svg');
  }

  .sidebar ul {
    padding: 0;
    margin: 0;
    list-style: none;
  }

  .sidebar li {
    margin: 0.5rem 0;
  }

  .sidebar:before {
    content: "Navigation";
    position: absolute;
    width: 13rem;
    transform: rotate(90deg) translate(40%, -6.5rem);
  }

  .sidebar:hover {
    transform: translate(0, -50%);
  }

  .explanation {
    @include contract;
    top: $console-p-row;
    left: $console-p-column;
    &:hover {
      @include expand;
    }
  }

  .participation {
    @include contract;
    bottom: $console-p-row;
    left: $console-p-column;
    &:hover {
      @include expand;
    }
  }

  .benefits {
    @include contract;
    bottom: $console-p-row;
    right: $console-p-column;
    &:hover {
      @include expand;
    }
  }  

  .requirements {
    @include contract;
    top: $console-p-row;
    right: $console-p-column;
    &:hover {
      @include expand;
    }
  }

  .wrapper {
    display: block;
  }

  footer {
    position: fixed;
    bottom: 0;
    left: 50vw;
    transform: translate(-50%, 0);
  }

  .design-selection {
    border: none;
  }

  @keyframes expand-console {
    0% {
      width: $console-w-narrow;
      max-height: $console-maxh-narrow;
      z-index: 3;
    }
    50% {
      width: $console-w-expanded;
      max-height: $console-maxh-narrow;
    }
    100% {
      width: $console-w-expanded;      
      max-height: $console-maxh-expanded;
      z-index: 3;
    }
  }

  @keyframes contract-console {
    100% {
      width: $console-w-narrow;
      max-height: $console-maxh-narrow;
      z-index: 3;
    }
    50% {
      width: $console-w-expanded;
      max-height: $console-maxh-narrow;
    }
    0% {
      width: $console-w-expanded;      
      max-height: $console-maxh-expanded;
      z-index: 3;
    }
  }
