    transform: translate(-13rem, -50%);
    transition: transform 2s ease-in-out;
    background-image: url('/assets/side-interface.svg');
  }

  .main div p {
    margin: 1rem;
  }

  header,
  footer,
  .summary,
  .preamble,
  .explanation,
  .participation,
  .benefits,
  .requirements,
  .sidebar {
    margin: 0;
  }

  .intro {
    position: fixed;
    top: 0;
    left: 50vw;
    transform: translate(-50%, -95%);
    transition: transform 2s ease-in-out;
  }

  .intro::after {
    content: "CSS Zen Garden";
  }

  .intro:hover {
    transform: translate(-50%, 0);
  }

  .intro:hover + .main{
    //transform: 
  }

  header > *{
    margin: 1rem;
  }

  

  .sidebar:before {
    content: "Navigation";
    position: absolute;
    width: 13rem;
    transform: rotate(90deg) translate(40%, -6.5rem);
  }

  .sidebar:hover {
    transform: translate(-$border-width, -50%);
  }

  .explanation {
    @include contract;
    top: $console-p-row;
    left: $console-p-column;
  }

  .participation {
    @include contract;
    bottom: $console-p-row;
    left: $console-p-column;
    
  }

  .benefits {
    @include contract;
    bottom: $console-p-row;
    right: $console-p-column;
  }  

  .requirements {
    @include contract;
    top: $console-p-row;
    right: $console-p-column;
  }

  .wrapper {
    display: block;
  }

  footer {
    position: fixed;
    bottom: 0;
    left: 50vw;
    transform: translate(-50%, 0);
  }

  .design-selection {
    border: none;
  }

  @keyframes expand-console {
    0% {
      width: $console-w-narrow;
      max-height: $console-maxh-narrow;
      z-index: 3;
    }
    50% {
      width: $console-w-expanded;
      max-height: $console-maxh-narrow;
    }
    99% {
      overflow: hidden;
    }
    100% {
      width: $console-w-expanded;      
      max-height: $console-maxh-expanded;
      z-index: 3;      
      overflow: auto;
    }
  }

  @keyframes contract-console {
    100% {
      width: $console-w-narrow;
      max-height: $console-maxh-narrow;
      z-index: 3;
    }
    50% {
      width: $console-w-expanded;
      max-height: $console-maxh-narrow;
    }
    0% {
      width: $console-w-expanded;      
      max-height: $console-maxh-expanded;
      z-index: 3;
