    -webkit-filter: blur(3px);
  }
}

.page-wrapper {
  display: flex;
  flex-direction: column;
  margin: 0 0.5rem;
}

.intro {
  order: 1;
  @include interface;
}

.sidebar {
  order: 2;
  padding: 0.75rem;
  background: $screen-tr;
  ul {
    padding: 0;
    margin: 0;
    list-style: none;
  }
  li {
    margin: 0.5rem 0;
  }
}

.main {
  order: 3;
  @include interface;
}



header,
footer,
.summary,
.preamble,
.explanation,
.participation,
.benefits,
.requirements,
.sidebar {
  border: $border-width double $color-border;
  margin: 1rem 0;
}

footer {
  padding: 1rem;
  border-bottom: none;
}

@media only screen and (min-width: $M) {
	.page-wrapper {
		margin: 0 5rem;
	}

	.wrapper {
		display: grid;
		grid-template-columns: 1fr 1fr;
    gap: 1rem;
	}

	.design-selection {
		grid-row: span 2;
		border-right-width: $border-width;
		border-right-style: solid;
    border-color: $color-border;
		align-self: stretch; 
	}

	.zen-resources {
		grid-column: 2 / 3;
	}

}

@media only screen and (min-width: $L) {

  html::before {
